import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
    selector: 'avans-nx-workshop-dashboard',
    templateUrl: './dashboard.component.html',
    styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
    featuredRecipes = [
      {
        id: '1',
        title: 'Spaghetti Carbonara',
        description: 'A classic Italian pasta dish made with eggs, cheese, pancetta, and pepper.',
        image: 'https://example.com/images/carbonara.jpg',
        time: 30,
      },
      {
        id: '2',
        title: 'Chocolate Cake',
        description: 'Rich and moist chocolate cake topped with creamy frosting.',
        image: 'https://example.com/images/chocolate-cake.jpg',
        time: 60,
      },
      {
        id: '3',
        title: 'Caesar Salad',
        description: 'Crisp romaine lettuce tossed with Caesar dressing, croutons, and parmesan cheese.',
        image: 'https://example.com/images/caesar-salad.jpg',
        time: 15,
      },
    ];

    constructor(private router: Router) {}

    ngOnInit(): void {
      // You can fetch the recipes here from an API or service.
    }

    viewRecipe(id: string): void {
      this.router.navigate(['/recipes', id]);
    }

    editRecipe(id: string): void {
      this.router.navigate(['/recipes', id, 'edit']);
    }

    isAdmin(): boolean {
      // Replace with your actual admin check logic
      return localStorage.getItem('role') === 'Admin';
    }
  }
