import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '@avans-nx-workshop/features';
import { IUserRegistration } from '@avans-nx-workshop/shared/api';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
})
export class RegisterComponent {
  registrationData: IUserRegistration = {
    name: '',
    emailAddress: '',
    password: '',
  };

  errorMessage: string | null = null;

  constructor(private authService: AuthService, private router: Router) {}

  register(): void {
    console.log('Attempting to register with data:', this.registrationData);

    this.authService.register(this.registrationData).subscribe({
      next: (response) => {
        console.log('Registration response from server:', response);

        const user = response?.results;
        if (user && user.token) {
          console.log('User successfully registered with Token:', user.token);
          this.router.navigate(['/login']); // Redirect to login page
        } else {
          console.warn(
            'Registration successful but missing token in the response:',
            response
          );
          this.errorMessage =
            'Registration successful, but token is missing. Please contact support.';
        }
      },
      error: (err) => {
        this.errorMessage = 'Registration failed. Please try again.';
        console.error('Registration error occurred:', err);

        // Log detailed error information if available
        if (err.error) {
          console.error('Backend error message:', err.error.message || err.error);
        }

        // Provide more detailed error messages to the user
        if (err.status === 409) {
          this.errorMessage = 'Email is already in use. Please use a different email.';
        } else if (err.status === 400) {
          this.errorMessage = 'Invalid registration data. Please check your input.';
        } else {
          this.errorMessage = 'An unexpected error occurred. Please try again later.';
        }
      },
    });
  }
}
<div class="register-container d-flex justify-content-center align-items-center">
    <div class="card p-4" style="width: 400px;margin-top: 10vh; margin-bottom: 10vh;">
        <h3 class="text-center mb-4">Register</h3>
        <form (ngSubmit)="register()">
            <div class="form-group mb-3">
                <input
                    id="name"
                    type="text"
                    class="form-control"
                    placeholder="Name"
                    [(ngModel)]="registrationData.name"
                    name="name"
                    required
                />
            </div>
            <div class="form-group mb-3">
                <input
                    id="emailAddress"
                    type="email"
                    class="form-control"
                    placeholder="Email Address"
                    [(ngModel)]="registrationData.emailAddress"
                    name="emailAddress"
                    required
                />
            </div>
            <div class="form-group mb-3">
                <input
                    id="password"
                    type="password"
                    class="form-control"
                    placeholder="Password"
                    [(ngModel)]="registrationData.password"
                    name="password"
                    required
                />
            </div>
            <button type="submit" class="btn btn-primary w-100 mb-3">Register</button>
            <div class="text-center">
                <a routerLink="/login" class="text-decoration-none">Already have an account? Login</a>
            </div>
        </form>
        <div *ngIf="errorMessage" class="error-message text-danger mt-3 text-center">
            {{ errorMessage }}
        </div>
    </div>
</div>
