import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '@avans-nx-workshop/features';
import { IUserRegistration } from '@avans-nx-workshop/shared/api';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  registrationData: IUserRegistration = {
    name: '',
    emailAddress: '',
    password: ''
  };

  errorMessage: string | null = null;

  constructor(private authService: AuthService, private router: Router) {}

  register(): void {
    this.authService.register(this.registrationData).subscribe({
      next: (user) => {
        console.log('Registration successful', user);
        this.router.navigate(['/login']); // Redirect to login page
      },
      error: (err) => {
        this.errorMessage = 'Registration failed. Please try again.';
        console.error('Registration error', err);
      }
    });
  }
}
<div class="register-container">
    <form (ngSubmit)="register()">
        <div class="form-group mb-3">
            <input
                id="name"
                type="text"
                class="form-control"
                placeholder="Name"
                [(ngModel)]="registrationData.name"
                name="name"
                required
            />
        </div>
        <div class="form-group mb-3">
            <input
                id="emailAddress"
                type="email"
                class="form-control"
                placeholder="Email Address"
                [(ngModel)]="registrationData.emailAddress"
                name="emailAddress"
                required
            />
        </div>
        <div class="form-group mb-3">
            <input
                id="password"
                type="password"
                class="form-control"
                placeholder="Password"
                [(ngModel)]="registrationData.password"
                name="password"
                required
            />
        </div>
        <br>
        <button type="submit" class="btn btn-primary">Register</button>
    </form>
    <div *ngIf="errorMessage" class="error-message">
        {{ errorMessage }}
    </div>
</div>
