import { Component, OnInit } from '@angular/core';
import { IUserInfo, UserRole, UserGender } from '@avans-nx-workshop/shared/api';
import { UserService } from '../user.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-user-details',
  templateUrl: './user-details.component.html',
})
export class UserDetailsComponent implements OnInit {
  userDetails!: IUserInfo;
  userId!: string;

  constructor(private userService: UserService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe((params) => {
      const id = params.get('id');
      if (id) {
        this.userId = id;
        this.fetchUserDetails(id);
      }
    });
  }

  fetchUserDetails(id: string): void {
    this.userService.getUserById(id).subscribe({
      next: (user) => {
        this.userDetails = user;
      },
      error: (err) => {
        console.error('Failed to fetch user details', err);
      },
    });
  }

  getRoleName(role: UserRole): string {
    const roleMapping = {
      [UserRole.Admin]: 'Admin',
      [UserRole.Guest]: 'Guest',
      [UserRole.Unknown]: 'Unknown',
    };
    return roleMapping[role] || 'Unknown';
  }

  getGenderName(gender: UserGender): string {
    const genderMapping = {
      [UserGender.Male]: 'Male',
      [UserGender.Female]: 'Female',
      [UserGender.None]: 'None',
      [UserGender.Unknown]: 'Unknown',
    };
    return genderMapping[gender] || 'Unknown';
  }
}
